{
  "title": "IndustryAnalysisOutput",
  "type": "object",
  "description": "Structured response for industry and competitive analysis based on customer data.",
  "properties": {
    "identifiedIndustry": {
      "type": "string",
      "description": "The primary industry sector identified for the customer."
    },
    "industryIdentification": {
      "type": "object",
      "description": "Details on how the industry was identified from the customer data.",
      "properties": {
        "sector": { "type": "string" },
        "subSector": { "type": "string" },
        "confidenceLevel": {
          "type": "number",
          "description": "Confidence score between 0 and 1.",
          "minimum": 0,
          "maximum": 1
        },
        "rationale": {
          "type": "string",
          "description": "Explanation of how the industry classification was derived from the input data."
        }
      },
      "required": ["sector", "confidenceLevel", "rationale"]
