    "industryDeepDive": {
      "type": "object",
      "description": "Comprehensive overview of the identified industry.",
      "properties": {
        "overview": { "type": "string" },
        "marketSizeAndGrowth": { "type": "string" },
        "keySegments": { "type": "string" },
        "keyPlayers": { "type": "string" },
        "growthDrivers": { "type": "string" },
        "challengesAndRisks": { "type": "string" },
        "opportunities": { "type": "string" }
