      "type": "array",
      "description": "List of key competitors in the identified industry.",
      "items": {
        "type": "object",
        "properties": {
          "name": { "type": "string" },
          "description": { "type": "string" },
          "marketPositioning": { "type": "string" },
          "strengths": { "type": "string" },
          "weaknesses": { "type": "string" },
          "strategicInitiatives": {
            "type": "string",
            "description": "Notable strategies, moves or investments (e.g., M&A, new products, geographic expansion)."
          }
        },
        "required": ["name", "marketPositioning"]
      }
    },
    "trendAnalysis": {
      "type": "object",
      "description": "Key trends impacting the industry.",
      "properties": {
        "technological": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "trend": { "type": "string" },
              "impact": { "type": "string" },
              "timeHorizon": { "type": "string" }
            },
            "required": ["trend", "impact"]
          }
        },
        "regulatory": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "trend": { "type": "string" },
              "impact": { "type": "string" }
            },
            "required": ["trend", "impact"]
          }
        },
        "economic": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "trend": { "type": "string" },
              "impact": { "type": "string" }
            },
            "required": ["trend", "impact"]
          }
        },
