const tasks = $input.all();
const isEmpty = (obj) => Object.keys(obj).length === 0;
const watchers = tasks.map((task) => { 
  if(task.json.assignees.length === 0) return {};
  return {
  "assignees": task?.json?.assignees
  }
}).filter(x => !isEmpty(x));
return watchers;

