=<!DOCTYPE html>
  <html>
  <head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
  </head>
  <body style="margin: 0; padding: 0; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Arial, sans-serif; background-color: #f3f4f6;">
  {{ (() => {
    // Helper Functions
      const getProductName = (interest) => {
        switch(interest) {
          case 'business-in-a-box':
            return 'Business-in-a-Box Products';
          case 'automation-packs':
            return 'Productized Automation Packs';
          case 'both':
            return 'Both Products';
          default:
            return 'Our Products';
        }
      };

  const getProductEmoji = (interest) => {
        switch(interest) {
          case 'business-in-a-box':
            return 'üì¶';
          case 'automation-packs':
            return '‚ö°';
          case 'both':
            return 'üì¶‚ö°';
          default:
            return '‚ú®';
        }
      };

      const shouldShowBusinessBox = (interest) => {
        return interest === 'business-in-a-box' || interest === 'both';
      };

      const shouldShowAutomationBox = (interest) => {
        return interest === 'automation-packs' || interest === 'both';
      };

      const getBusinessBoxHTML = () => {
        return `<div style="background: linear-gradient(135deg, #f0f9ff 0%, #e0e7ff 100%); padding: 25px; border-radius: 12px; margin-bottom: 20px; border: 1px solid #e0e7ff;">
          <h3 style="margin: 0 0 15px 0; color: #1e40af; font-size: 20px;">üì¶ Business-in-a-Box Products</h3>
          <p style="margin: 0; color: #1e3a8a; line-height: 1.6;">
            Prebuilt automation templates for common workflows, ready to deploy in minutes. Industry-specific packs for real estate, accounting, salons, and home services with pre-configured integrations,
  documentation, and training included. One-time purchase, yours forever.
          </p>
        </div>`;
      };

      const getAutomationBoxHTML = () => {
        return `<div style="background: linear-gradient(135deg, #fef3c7 0%, #fde68a 100%); padding: 25px; border-radius: 12px; margin-bottom: 20px; border: 1px solid #fbbf24;">
          <h3 style="margin: 0 0 15px 0; color: #92400e; font-size: 20px;">‚ö° Productized Automation Packs</h3>
          <p style="margin: 0; color: #78350f; line-height: 1.6;">
            Lightweight, quick-start automations for common pain points like lead capture & follow-up, invoice automation, and customer onboarding. Fixed scope, fixed price, fast delivery.
          </p>
        </div>`;
      };

      const interest = $json.interest;
      const productName = getProductName(interest);
      const productEmoji = getProductEmoji(interest);

  // Build the email HTML
      return `
    <div style="max-width: 600px; margin: 0 auto; padding: 20px;">
      <div style="background: white; border-radius: 12px; overflow: hidden; box-shadow: 0 4px 6px rgba(0,0,0,0.1);">

        <!-- Header -->
        <div style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 40px 30px; text-align: center;">
          <div style="font-size: 50px; margin-bottom: 10px;">üéâ</div>
          <h1 style="margin: 0; font-size: 28px; font-weight: 700;">You're on the List!</h1>
          <p style="margin: 10px 0 0 0; font-size: 14px; opacity: 0.9;">Thanks for your interest in AutoMagicly</p>
        </div>

        <!-- Body -->
        <div style="padding: 30px;">

          <!-- Confirmation Box -->
          <div style="background: #f0fdf4; border-left: 4px solid #10b981; padding: 20px; border-radius: 8px; margin-bottom: 30px;">
            <p style="margin: 0; color: #065f46; font-size: 16px;">
              ‚úì You've been added to the waitlist for <strong>${productName}</strong>
            </p>
          </div>

          <!-- Product Details -->
          ${shouldShowBusinessBox(interest) ? getBusinessBoxHTML() : ''}
          ${shouldShowAutomationBox(interest) ? getAutomationBoxHTML() : ''}

          <!-- Benefits Section -->
          <h3 style="color: #111827; font-size: 20px; margin: 30px 0 15px 0;">üéÅ You'll Be Among the First to Know!</h3>
          <p style="color: #4b5563; margin: 0 0 20px 0; line-height: 1.6;">
            We'll email you when <strong>${productName}</strong> ${interest === 'both' ? 'launch' : 'launches'} with:
          </p>

          <div style="background: #f9fafb; border-radius: 8px; padding: 20px; margin-bottom: 30px;">
            <table style="width: 100%; border-collapse: collapse;">
              <tr>
                <td style="padding: 8px 0; color: #374151;">
                  <span style="color: #10b981; font-size: 20px; margin-right: 10px;">‚úì</span>
                  <strong>Early Access</strong> - Before public release
                </td>
              </tr>
              <tr>
                <td style="padding: 8px 0; color: #374151;">
                  <span style="color: #10b981; font-size: 20px; margin-right: 10px;">‚úì</span>
                  <strong>Special Launch Pricing</strong> - Exclusive discount
                </td>
              </tr>
              <tr>
                <td style="padding: 8px 0; color: #374151;">
                  <span style="color: #10b981; font-size: 20px; margin-right: 10px;">‚úì</span>
                  <strong>Exclusive Onboarding</strong> - Personalized setup support
                </td>
              </tr>
            </table>
          </div>

          <!-- CTA Section -->
          <div style="background: linear-gradient(135deg, #fef3c7 0%, #fde68a 100%); padding: 25px; border-radius: 12px; text-align: center; margin-bottom: 20px; border: 2px solid #fbbf24;">
            <h3 style="margin: 0 0 10px 0; color: #92400e; font-size: 18px;">Want to Start Automating Right Away?</h3>
            <p style="margin: 0 0 20px 0; color: #78350f; font-size: 14px;">
              Don't wait! Schedule a free AI audit and start saving time today.
            </p>
            <a href="https://automagicly.com#booking" style="display: inline-block; background: #2563eb; color: white; padding: 14px 32px; border-radius: 8px; text-decoration: none; font-weight: 700; 
  font-size: 16px; box-shadow: 0 4px 6px rgba(37, 99, 235, 0.3);">
              üìÖ Schedule Free AI Audit
            </a>
          </div>

          <!-- Footer -->
          <div style="text-align: center; padding-top: 20px; border-top: 1px solid #e5e7eb;">
            <p style="color: #6b7280; margin: 0; line-height: 1.6;">
              Talk soon!<br>
              <strong style="color: #111827;">The AutoMagicly Team</strong>
            </p>
            <p style="color: #9ca3af; font-size: 12px; margin: 15px 0 0 0;">
              P.S. Reply to this email if you have any questions. We read every message! üí¨
            </p>
          </div>

        </div>
      </div>

      <!-- Unsubscribe Footer -->
      <div style="text-align: center; padding: 20px; color: #9ca3af; font-size: 12px;">
        <p style="margin: 0;">
          AutoMagicly - AI Automation for Your Business<br>
          You're receiving this because you joined our waitlist
        </p>
      </div>
    </div>
  `;
  })() }}
  </body>
  </html>

